function calculate(){let e=parseInt(document.getElementById("current-percent").value,10),t=parseInt(document.getElementById("min-percent").value,10),n=parseInt(document.getElementById("final-percent").value,10),r=(t/100-e/100*(1-n/100))/(n/100)*100;document.getElementById("total").value=r.toPrecision(5)+"%",isNaN(r)||console.log("Everything was fine when calculating when user entered numbers!"),isNaN(r)&&console.error("There was an error when calculating the numbers entered! Try Again!"),r>100&&(alert("Won't be able to get that desired score without extra credit! Sorry!"),console.error("Sorry, but is not possible to get your desired grade without extra credit being on the final!"))}